const API_URL = "http://localhost:8080/todolist";

// Get all tasks
export const getTaskToBeDone = async () => {
    const response = await fetch(API_URL);
    return response.json();
};

//Get task by Id
export const getTaskById = async (id) =>{
    const response = await fetch(`${API_URL}/${id}`);
    return response.json();
}

// Add a new task 
export const addTaskToList = async (taskToBeAdded) => {
  const response = await fetch(API_URL, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ taskToBeAdded, taskcompleted: true }),
  });
  return response.json();
};

//Update a existing task
export const updateExistingTask = async (id,taskToBeUpdate) =>{
    const response = await fetch(`${API_URL}/${id}`,{
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ taskToBeUpdate, taskcompleted: true })
    });
    return response.json();
}

// Delete task (DELETE)
export const deleteDoneTask = async (id) => {
  await fetch(`${API_URL}/${id}`, { method: "DELETE" });
};
